    def order(self):
        self.a=(str(self.lineEdit.text())).split(',')
        self.rs=0
        for i in self.a:
            i=int(i)
            if i==29 or i==30:
                self.rs=self.rs+15
            elif i==1 or i==31 or i==32:
                self.rs=self.rs+20
            elif i==2 or i==3 or i==4:
                self.rs=self.rs+25
            elif i==7 or i==8:
                self.rs=self.rs+50
            elif i==43 or i==44 or i==45 or i==46:
                self.rs=self.rs+60
            elif i==9 or i==10:
                self.rs=self.rs+70
            elif i==33 or i==34:
                self.rs=self.rs+90
            elif i==37:
                self.rs=self.rs+100
            elif i==11 or i==12 or i==13 or i==14 or i==15 or i==16 or i==17 or i==35 or i==36 or i==38:
                self.rs=self.rs+110
            elif i==18 or i==19:
                self.rs=self.rs+120
            elif i==40 or i==41:
                self.rs=self.rs+130
            elif i==20 or i==21 or i==22 or i==23 or i==24 or i==25 or i==26 or i==42:
                self.rs=self.rs+140
            elif i==28:
                self.rs=self.rs+150
            else:
                self.l6.setText("No such Item")
        sql="insert into Food values('{}','{}',{},'{}')".format(self.c,(str(self.lineEdit.text())),self.rs,"No")
        cur.execute(sql)
        con.commit()
        self.l6.setText("Succesfully Booked.")
